import styles from './Home.module.css'
import { useEffect, useState } from 'react'
import Container from '../layouts/Container'

import DeleteBtn from '../update/DeleteBtn'

import Input from '../form/Input'
import SubmitButton from '../form/SubmitButton'

function Home() {

    const [product, setProduct]= useState([])
    
    useEffect(() => {
        fetch("http://ec2-18-230-76-165.sa-east-1.compute.amazonaws.com:3000/product", {
            method: "GET", 
            headers:  {
                'Content-Type': 'application/json'
            }
        })
        .then((res) => res.json())
        .then((data) => {
            
            setProduct(data)
            
        })
        .catch((err) => console.log(err))
    }, [0])


    function productUpdate(e) {
        useEffect(() => {
        console.log(e.target.value)
        const productId = e.target.value
            fetch(`http://ec2-18-230-76-165.sa-east-1.compute.amazonaws.com:3000/product/${productId}`, {
                method: "GET", 
                headers:  {
                    'Content-Type': 'application/json'
                }
            })
            .then((res) => res.json())
            .then((data) => {
                
                console.log(data)
                
            })
            .catch((err) => console.log(err))
        }, [0])
    }

    return (
        <section className={styles.homeSection}>
            <Container>
                {
                    product.map((item) =>{
                        
                        

                        return (
                            <ul>
                                <li>Nome: {item.name}</li>
                                <li>Preço: R${item.price}</li>
                                <li>Codigo de barras: {item.code}</li>
                                <li>Quantidade no estoque: {item.quantity}</li>
                                <li>Estoque mínimo: {item.minimum_stock}</li>
                                <li>Qualidade: {item.quality}</li>
                                <li>Validade: {item.validate}</li>
                                <button onClick={productUpdate} value={item._id}>Atualizar Produto</button>
                                <DeleteBtn id={item._id}/>
                            </ul>
                        )
                    })
                }
                <form >
                    <Input type='text' text='Nome do produto' name='name' placeholder='Insira o nome do produto'  value={product.name ? product.name : ''}/>
                    <Input type='number' text='Preço do produto' name='price' placeholder='Insira o Preço do produto' value={product.price ? product.price : ''}/>
                    <Input type='number' text='Código do produto' name='code' placeholder='Insira o Código do produto'  value={product.code ? product.code : ''}/>
                    <Input type='number' text='Estoque do produto' name='quantity' placeholder='Insira a quantidade do produto'  value={product.quantity ? product.quantity : ''}/>
                    <Input type='number' text='Estoque mínimo do produto' name='minimum_stock' placeholder='Insira o Estoque mínimo do produto'  value={product.minimum_stock ? product.minimum_stock : ''}/>
                    <Input type='text' text='Qualidade do produto' name='quality' placeholder='Insira a qualidade do produto' value={product.quality ? product.quality : ''}/>
                    <Input type='date' text='Validade do produto' name='validate' placeholder='Insira a validade do produto'  value={product.validate ? product.validate : ''}/>
                    <SubmitButton text='Atualizar Produto'/>
                    
                </form>
            </Container>
        </section>
    )
    
}

export default Home
